entry.value:
SELECT *
FROM sales.transactions
WHERE DATE(created_date) = DATE(DATE_SUB(NOW(), INTERVAL 1 DAY));

import org.apache.nifi.processor.io.StreamCallback
import java.nio.charset.StandardCharsets
import java.text.SimpleDateFormat
import java.util.Random

def flowFile = session.create()

if (flowFile) {
    // Cấu hình ngày bắt đầu và ngày kết thúc
    def startDateStr = "2023-01-01"
    def endDateStr = "2024-12-30"
    def dateFormat = new SimpleDateFormat("yyyy-MM-dd")
    def isoDateFormat = new SimpleDateFormat("yyyy-11-dd'T'00:00:00.000")
    def partitionDateFormat = new SimpleDateFormat("yyyyMMdd")
    def random = new Random()
    def startDate = dateFormat.parse(startDateStr)
    def endDate = dateFormat.parse(endDateStr)

    def currentDate = startDate
    def dateList = []

    // Lặp qua khoảng thời gian
    while (currentDate <= endDate) {
        def isoDate = isoDateFormat.format(currentDate)
        def partitionDate = partitionDateFormat.format(currentDate)

        // Tạo HH:mm:ss ngẫu nhiên cho process_date
        def randomHour = random.nextInt(24)
        def randomMinute = random.nextInt(60)
        def randomSecond = random.nextInt(60)

        // Định dạng process_date với giờ ngẫu nhiên
        def processDate = String.format("%s %02d:%02d:%02d", dateFormat.format(currentDate), randomHour, randomMinute, randomSecond)

        // Tạo giá trị hour_id ngẫu nhiên
        def hourId = String.format("%02d", randomHour)

        dateList << [isoDate: isoDate, partitionDate: partitionDate, processDate: processDate, hourId: hourId]
        currentDate += 1
    }

    // Tạo FlowFile cho từng ngày
    dateList.each { dateItem ->
        def newFlowFile = session.create(flowFile)
        // Gán các thuộc tính date, partition, process_date, và hour_id
        newFlowFile = session.putAttribute(newFlowFile, "date", dateItem.isoDate)
        newFlowFile = session.putAttribute(newFlowFile, "partition", dateItem.partitionDate)
        newFlowFile = session.putAttribute(newFlowFile, "process_date", dateItem.processDate)
        newFlowFile = session.putAttribute(newFlowFile, "hour_id", dateItem.hourId)
        session.transfer(newFlowFile, REL_SUCCESS)
    }

    // Xóa flowFile ban đầu
    session.remove(flowFile)
}

